import logo from './logo.svg';
import './App.css';
import SpotifyWebApi from "spotify-web-api-js"

import React, {useState, useEffect} from "react"


const spotifyApi = new SpotifyWebApi


var _baseUri = 'https://api.spotify.com/v1';
var _accessToken = null;
var _promiseImplementation = null;

var WrapPromiseWithAbort = function (promise, onAbort) {
  promise.abort = onAbort;
  return promise;
};

var _promiseProvider = function (promiseFunction, onAbort) {
  var returnedPromise;
  if (_promiseImplementation !== null) {
    var deferred = _promiseImplementation.defer();
    promiseFunction(
      function (resolvedResult) {
        deferred.resolve(resolvedResult);
      },
      function (rejectedResult) {
        deferred.reject(rejectedResult);
      }
    );
    returnedPromise = deferred.promise;
  } else {
    if (window.Promise) {
      returnedPromise = new window.Promise(promiseFunction);
    }
  }

  if (returnedPromise) {
    return new WrapPromiseWithAbort(returnedPromise, onAbort);
  } else {
    return null;
  }
};

var _extend = function () {
  var args = Array.prototype.slice.call(arguments);
  var target = args[0];
  var objects = args.slice(1);
  target = target || {};
  objects.forEach(function (object) {
    for (var j in object) {
      if (object.hasOwnProperty(j)) {
        target[j] = object[j];
      }
    }
  });
  return target;
};

var _buildUrl = function (url, parameters) {
  var qs = '';
  for (var key in parameters) {
    if (parameters.hasOwnProperty(key)) {
      var value = parameters[key];
      qs += encodeURIComponent(key) + '=' + encodeURIComponent(value) + '&';
    }
  }
  if (qs.length > 0) {
    // chop off last '&'
    qs = qs.substring(0, qs.length - 1);
    url = url + '?' + qs;
  }
  return url;
};

var _performRequest = function (requestData, callback) {
  var req = new XMLHttpRequest();

  var promiseFunction = function (resolve, reject) {
    function success(data) {
      if (resolve) {
        resolve(data);
      }
      if (callback) {
        callback(null, data);
      }
    }

    function failure() {
      if (reject) {
        reject(req);
      }
      if (callback) {
        callback(req, null);
      }
    }

    var type = requestData.type || 'GET';
    req.open(type, _buildUrl(requestData.url, requestData.params));
    if (_accessToken) {
      req.setRequestHeader('Authorization', 'Bearer ' + _accessToken);
    }

    req.onreadystatechange = function () {
      if (req.readyState === 4) {
        var data = null;
        try {
          data = req.responseText ? JSON.parse(req.responseText) : '';
        } catch (e) {
          console.error(e);
        }

        if (req.status >= 200 && req.status < 300) {
          success(data);
        } else {
          failure();
        }
      }
    };

    if (type === 'GET') {
      req.send(null);
    } else {
      var postData = null;
      if (requestData.postData) {
        if (requestData.contentType === 'image/jpeg') {
          postData = requestData.postData;
          req.setRequestHeader('Content-Type', requestData.contentType);
        } else {
          postData = JSON.stringify(requestData.postData);
          req.setRequestHeader('Content-Type', 'application/json');
        }
      }
      req.send(postData);
    }
  };

  if (callback) {
    promiseFunction();
    return null;
  } else {
    return _promiseProvider(promiseFunction, function () {
      req.abort();
    });
  }
};

var _checkParamsAndPerformRequest = function (
  requestData,
  options,
  callback,
  optionsAlwaysExtendParams
) {
  var opt = {};
  var cb = null;

  if (typeof options === 'object') {
    opt = options;
    cb = callback;
  } else if (typeof options === 'function') {
    cb = options;
  }

  // options extend postData, if any. Otherwise they extend parameters sent in the url
  var type = requestData.type || 'GET';
  if (type !== 'GET' && requestData.postData && !optionsAlwaysExtendParams) {
    requestData.postData = _extend(requestData.postData, opt);
  } else {
    requestData.params = _extend(requestData.params, opt);
  }
  return _performRequest(requestData, cb);
};

var Constr = function () {};

Constr.prototype = {
  constructor: SpotifyWebApi
};






const getTokenFromURL = () => {
  return window.location.hash
  .substring(1)
  .split("&")
  .reduce ((initial, item) => {
    let parts = item.split("=");
    initial [parts[0]] = decodeURIComponent(parts[1]);
    return initial
  },{});
}



function App() {
  const [spotifyToken, setSpotifyToken] =useState("");
  const [nowPlaying, setNowPlaying] = useState({});
  const [loggedIn, setLoggedIn] = useState(false)



    useEffect(() => {
      if (loggedIn===false){
    console.log("This is what we dericed from the URL: " , getTokenFromURL())
    const spotifyToken = getTokenFromURL().access_token
    console.log("This is our spotify token", spotifyToken);

    if (spotifyToken) {
      setSpotifyToken(spotifyToken)
      spotifyApi.setAccessToken(spotifyToken)
      Constr.prototype.getMyTopArtists = function (options, callback) {
        var requestData = {
          url: _baseUri + '/me/top/artists'
        };
        return _checkParamsAndPerformRequest(requestData, options, callback);
      };

      // spotifyApi.getMe().then((user) => {
      //   console.log(user)
      // })
      setLoggedIn(true)

    }
  }


  },[]);




const getNowPlaying = () => {
  spotifyApi.getMyCurrentPlaybackState().then((response) => {
    console.log(response);
    setNowPlaying({
      name: response.item.name,
      albumArt: response.item.album.images[0].url
    })
  })
}


  return (
    <div className="App">
     {!loggedIn && <a href="http://localhost:8888">Login to Spotify</a>}
    {loggedIn &&(
      <>
      <div>NowPlaying:{nowPlaying.name}
      <div>
        <img src={nowPlaying.albumArt} style={{height:150}}/>
       </div>
       </div>
       </>
    )}
    {loggedIn && (
      <button onClick={()=>getNowPlaying()}>Check Now Playing</button>
    )

    }
     </div>
  );
}

export default App;
